sys_script.script:
(function executeRule(current, previous /*null when async*/) {
gs.addInfoMessage("create jira user");
	// Add your code here

	try { 
 var r = new sn_ws.RESTMessageV2('x_58872_needit.Creating JIRA user', 'Createuser');
		 r.setStringParameterNoEscape('email', current.email);
         r.setStringParameterNoEscape('password', current.user_password);
		 r.setStringParameterNoEscape('name', current.first_name);
		 r.setStringParameterNoEscape('displayName', current.user_name);

//override authentication profile 
//authentication type ='basic'/ 'oauth2'
//r.setAuthentication(authentication type, profile name);

//set a MID server name if one wants to run the message on MID
//r.setMIDServer('MY_MID_SERVER');

//if the message is configured to communicate through ECC queue, either
//by setting a MID server or calling executeAsync, one needs to set skip_sensor
//to true. Otherwise, one may get an intermittent error that the response body is null
//r.setEccParameter('skip_sensor', true);

 var response = r.execute();
 gs.addInfoMessage("rest message sent" + response );
 var responseBody = response.getBody();
 gs.addInfoMessage("response body" + responseBody );
 var httpStatus = response.getStatusCode();
		current.last_name = httpStatus;
 gs.addInfoMessage("httpstatus" + httpStatus );
}
catch(ex) {
var message = ex.message;
}
})(current, previous);
