sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	if(g_form.isNewRecord()){
    var getEmailAddr = new GlideAjax('DateTime');   
    getEmailAddr.addParam('sysparm_name','WhenNeeded');   
    getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_string_1'));    
    getEmailAddr.getXML(populateWhenNeeded);		
	}
    // When the response is back from the server
    function populateWhenNeeded(response){
        var datePlus5 = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('short_description');
        g_form.setValue('short_description',datePlus5);
	}

}
